import { Injectable } from '@angular/core';
import { Product } from '../models/product';

@Injectable({
  providedIn: 'root',
})
export class BasketService {
  basket: Product[] = [];
  isBasket: boolean = false;
  getAmount(): number {
    let amount: number = 0;
    this.basket.forEach((element: Product) => {
      amount = amount + (element.discount? element.price * 0.8 : element.price)
    });
    return amount
  }
}

import { Injectable } from '@angular/core';
import { TypeProductEnum } from '../models/type-product.enum';


@Injectable({
  providedIn: 'root',
})
export class PageService {
  routes: string[] = ['/',TypeProductEnum.Clothes,TypeProductEnum.Shoes,TypeProductEnum.Accessories]
  page: number = 0;

  setPage(router: string) {
    this.routes.forEach((element: string, index: number) => {
        if (router === element) {
          this.page = index;
        }
    });
  }
}

