import {Component, OnInit} from '@angular/core';
import {FormControl} from '@angular/forms';
import { Router } from '@angular/router';
import {Observable} from 'rxjs';
import {map, startWith} from 'rxjs/operators';
import { Product } from 'src/app/core/models/product';
import { TypeProductEnum } from 'src/app/core/models/type-product.enum';
import { BasketService } from 'src/app/core/services/basket.service';
import { PageService } from 'src/app/core/services/page.service';
import { products } from '../../../assets/data';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent implements OnInit {
  id: string = '';
  control = new FormControl();
  routes: string[] = ['/',TypeProductEnum.Clothes,TypeProductEnum.Shoes,TypeProductEnum.Accessories]
  products: Product[] = products;
  filteredProducts?: Observable<Product[]>;

  constructor(private router: Router, public basket: BasketService, public pageService: PageService) {}

  ngOnInit(): void {
    this.filteredProducts = this.control.valueChanges
      .pipe(
        startWith(''),
        map(product => product ? this.filterProduct(product) : this.products.slice())
      );
  }

  routerChange(ev: number): void {
    if (ev === 0) {
      this.router.navigate([this.routes[ev]])
    } else {
      this.router.navigate(['/products',this.routes[ev]])
    }
  }

  private filterProduct(value: string): Product[] {
    const filterValue = value.toLowerCase();
    return this.products.filter(product => product.name.toLowerCase().includes(filterValue));
  }

  open(id:string) {
    this.router.navigate([`/product/${id}`])
  }
}

