import { BasketService } from 'src/app/core/services/basket.service';
import { PageService } from 'src/app/core/services/page.service';
import { products } from '../../../../assets/data';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.scss']
})
export class ProductListComponent {
  id: string = '';
  products: Product[] = products;
  constructor(private activatedRoute: ActivatedRoute, private router: Router, private pageService: PageService, public basketService: BasketService) {
    router.events.subscribe(() => {
      window.scrollTo({ top: 0, behavior: 'smooth' });
      this.id = activatedRoute.snapshot.paramMap.get('id') || '';
      pageService.setPage(this.id);
      if (this.id === TypeProductEnum.Basket){
        basketService.isBasket = true;
      } else {
        basketService.isBasket = false;
        this.products = products.filter((el: Product)=> el.type === this.id);
      }
    })
  }
}

